import React from "react";

class Form extends React.Component {
  constructor() {
    super()

    this.state = {
      house: '',
      warrior: '',
      nivel: '',
      description: ''
    }
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ 
      [name]: value 
    })
  }

  render() {
    return(
      <div>
        <h1>Meu Form</h1>
        <form className='form'>
          <select name='house' onChange={this.handleChange}> 
            <option value='fire'>Fogo</option>
            <option value='water'>Água</option>
            <option value='earth'>Terra</option>
            <option value='air'>Ar</option>
          </select>
          <input name='warrior' type='text' placeholder='Nome' onChange={this.handleChange} value={this.state.warrior}></input>
          <input name='nivel' type='number' placeholder='Nível de graduação. Máx 5'></input>
          <textarea name='description' placeholder='Conte sua história'></textarea>
        </form>
      </div>
    );
  }

}

export default Form;
